import React, {useState} from 'react'
import Navbar from '../../components/Navbar';
import axios from 'axios';
import Homepage from '../homepage/Homepage';

function Login() {

  const [email,setEmail] = useState('');
    const [password,setPassword] = useState('');
    const history = useNavigate();
    const[rows,setRows] = useState([]);
    useEffect(()=>{
            const localRows = localStorage.getItem('user-info')
        if(localRows){
            setRows(JSON.parse(localRows))
            history("/home")
        }

    }, [rows])

    async function login(){
        let item = {email,password}
        let result = await fetch("http://localhost:8080/api/students/create",{
                method : 'POST',
                headers:{
                    "Content-Type": "application/json",
                    "Accept" : "application/json"
                },
                body:JSON.stringify(item)
        });
        result =  result.json();
        localStorage.setItem("user-info",JSON.stringify(result))
        history("/home")
       
    }

    return (
    
        <div className='container'>

        <div className='row vh-100 align-items-center justify-content-center'>
            <div className='col-sm-8 col-md-6 col-lg-4 bg-white rounded p-4 shadow'>
                <div className='row justify-content-center mb-4'>
                    <img src='https://upb.ro/wp-content/uploads/2020/06/logo200upb.jpg' className='w-50'/>
                </div>
                <form onSubmit={submitHandler} >
                    <div className='mb-4'>
                        <label for="email" className='form-label'>Email Address</label>
                        <input type='email'
                         className="form-control"
                          id="email"
                         aria-describedby="email"
                         value={username}
                         onChange={(e) => setUserName(e.target.value)}
                         
                         />
                    </div>
                    <div className='mb-4'>
                        <label for="password" className='form-label'>Password</label>
                        <input type='password' 
                        className="form-control" 
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        />
                    </div>
                    <button type='submit' id='btn' className='btn btn-primary w-100 mt-4' onClick={login}>Login</button>
                </form>
            </div>
        </div>

        </div>
    
  )
}

export default Login;
