import React, {useState} from 'react'
import Navbar from '../../components/Navbar';

function Login() {

    const [emailError, setEmailError] = useState('');
const [passwordError, setPasswordError] = useState(''); 
  
    const onSubmit = (e) => {
        e.preventDefault();
    
        const formData = new FormData(e.target);
    
        fetch("http://localhost:8080/api/students", {
          method: "POST",
          headers: {
            Accept: "application/json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            email: formData.get('email'),
            password: formData.get('password'),
          }),
        })
          .then((response) => response.json())
          .then((data) => {
            if(data.fieldErrors) {
              data.fieldErrors.forEach(fieldError => {
                if(fieldError.field === 'email'){
                  setEmailError(fieldError.message);
                }
    
                if(fieldError.field === 'password'){
                  setPasswordError(fieldError.message);
                }
              });
            } else {
              alert("Success !!");
            }
          })
          .catch((err) => err);
      }
  
  
  
    return (
    
    <div className='container'>

        <div className='row vh-100 align-items-center justify-content-center'>
            <div className='col-sm-8 col-md-6 col-lg-4 bg-white rounded p-4 shadow'>
                <div className='row justify-content-center mb-4'>
                    <img src='https://upb.ro/wp-content/uploads/2020/06/logo200upb.jpg' className='w-50'/>
                </div>
                <form onSubmit={onSubmit}>
                    <div className='mb-4'>
                        <label for="email" className='form-label'>Email Address</label>
                        <input type='email' className="form-control" id="email" aria-describedby="email"/>
                    </div>
                    <div className='mb-4'>
                        <label for="password" className='form-label'>Password</label>
                        <input type='password' className="form-control" id="password"/>
                    </div>
                    <button type='submit' className='btn btn-primary w-100 mt-4'>Login</button>
                </form>
            </div>
        </div>

        </div>
    
  )
}

export default Login;
